// src/index.js
import React from 'react';
import ReactDOM from 'react-dom/client';
import axios from 'axios';
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';



import Cookies from 'js-cookie';
axios.defaults.withCredentials = true;

axios.interceptors.request.use((config) => {
  const token = Cookies.get('csrftoken');
  if (token) {
    config.headers['X-CSRFToken'] = token;
  }
  return config;
});

// Bootstrap y estilos
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min.js';
import 'bootstrap-icons/font/bootstrap-icons.css';
import './estilos/personalizado.css';

// Todas las llamadas a /api/... van a Django (ajusta si usas otro host/puerto)
axios.defaults.baseURL = process.env.REACT_APP_API_URL || 'http://34.227.79.113:8000';
axios.defaults.headers.common['Accept'] = 'application/json';
// axios.defaults.withCredentials = true; // si usas sesión/CSRF

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);

reportWebVitals();
